import com.sun.org.apache.xalan.internal.xsltc.runtime.output.TransletOutputHandlerFactory;/** * @author xmy * @date 2021/4/14 6:46 下午 *///验证回文串//"A man, a plan, a canal: Panama"public class PalindromeDemo {    public boolean isPalindrome(String s) {        if (s == null) {            return false;        }        s = s.toLowerCase();        int l = s.length();        StringBuilder str = new StringBuilder(l);        for (char c : s.toCharArray()) {            if ((c >= '0' && c <= '9') || (c >= 'a' && c <= 'z')) {                str.append(c);            }        }        System.out.println("当前的字符串为" + str);        return str.toString().equals(str.reverse().toString());    }    public static void main(String[] args) {        String s = "A man, a plan, a canal: Panama";        PalindromeDemo palindromeDemo = new PalindromeDemo();        System.out.println(palindromeDemo.isPalindrome(s));    }}